/**
 * Enqueue Google Web Fonts.
 */
function zoacres_theme_default_fonts_url() {
    $font_url = '';
     
    /*
    Translators: If there are characters in your language that are not supported
    by chosen font(s), translate this to 'off'. Do not translate into your own language.
     */
    if ( 'off' !== _x( 'on', 'Google font: on or off', 'zoacres' ) ) {
        $font_url = add_query_arg( 'family', urlencode( 'Hind:400|Poppins:600,500&subset=latin' ), "//fonts.googleapis.com/css" ); 
    }
    return $font_url;
}
function zoacres_enqueue_google_web_fonts() {
	wp_enqueue_style( 'google-fonts', zoacres_theme_default_fonts_url(), array(), null, 'all' );
}
function zoacres_default_theme_options(){
	$cur_theme = wp_get_theme();
	$zoacres_options = get_option( 'zoacres_options' );
	$logo_url = !empty( $zoacres_options ) && isset( $zoacres_options['logo']['url'] ) && $zoacres_options['logo']['url'] != '' ? $zoacres_options['logo']['url'] : '';
	if ( empty( $logo_url ) ){

		$input_val = zoacres_default_theme_values();
		$zoacres_options = json_decode( $input_val, true );
		update_option( 'zoacres_options', $zoacres_options );
		zoacres_save_theme_options();

	}
}
if ( ! class_exists( 'ZoacresRedux' ) ) {
	add_action( 'wp_enqueue_scripts', 'zoacres_enqueue_google_web_fonts' );
	add_action("after_switch_theme", "zoacres_default_theme_options", 10);
}
